@bot.message_handler(commands=['start', 'main', 'hello'])
def main(message):
    markup = types.InlineKeyboardMarkup()
    button_readnda = types.InlineKeyboardButton('–ü—Ä–æ—á–∏—Ç–∞—Ç—å –ø–æ–ª–∏—Ç–∏–∫—É –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏', callback_data='start_read_nda')
    markup.add(button_readnda)
    bot.send_message(message.chat.id, f'–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å, {message.from_user.last_name} {message.from_user.first_name} –≤ <b>–ù–æ–º–µ—Ä–æ–∞–≤—Ç–æ –†–ëüáßüáæ</b>!''\n \n–≠—Ç–æ ‚Äî <b>–µ–¥–∏–Ω—Å—Ç–≤–µ–Ω–Ω—ã–π</b> –≤ –ë–µ–ª–∞—Ä—É—Å–∏ Telegram-–±–æ—Ç –¥–ª—è <em>—Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏</em> –∏ –ø–æ–ª—É—á–µ–Ω–∏—è <em>–∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏</em> –æ–± –∞–≤—Ç–æ –∏ –µ–≥–æ –≤–ª–∞–¥–µ–ª—å—Ü–∞ –≤ –†–µ—Å–ø—É–±–ª–∏–∫–µ –ë–µ–ª–∞—Ä—É—Å—å! \n \n –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, —Å–Ω–∞—á–∞–ª–∞ –æ–∑–Ω–∞–∫–æ—Ç–µ—Å—å —Å –ø–æ–ª–∏—Ç–∏–∫–æ–π –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏ ‚¨á', parse_mode='html', reply_markup=markup)

@bot.callback_query_handler(func=lambda call: True)
def callback(call):
    if call.message:
        if call.data == 'start_read_nda':
            bot.send_message(call.message.chat.id, '–ü–æ–ª–∏—Ç–∏–∫–∞ –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏ "–ù–æ–º–µ—Ä–æ–∞–≤—Ç–æ –†–ëüáßüáæ" \n1. –í–≤–µ–¥–µ–Ω–∏–µ: –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ "–ù–æ–º–µ—Ä–æ–∞–≤—Ç–æ –†–ëüáßüáæ". –ú—ã —Ü–µ–Ω–∏–º –≤–∞—à—É –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç—å –∏ —Å—Ç—Ä–µ–º–∏–º—Å—è –∑–∞—â–∏—â–∞—Ç—å –≤–∞—à–∏ –ø–µ—Ä—Å–æ–Ω–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ. –≠—Ç–∞ –ü–æ–ª–∏—Ç–∏–∫–∞ –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏ –æ–±—ä—è—Å–Ω—è–µ—Ç, –∫–∞–∫ –º—ã —Å–æ–±–∏—Ä–∞–µ–º, –∏—Å–ø–æ–ª—å–∑—É–µ–º –∏ –∑–∞—â–∏—â–∞–µ–º –≤–∞—à—É –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é, –∫–æ–≥–¥–∞ –≤—ã –∏—Å–ø–æ–ª—å–∑—É–µ—Ç–µ –Ω–∞—à–µ–≥–æ –±–æ—Ç–∞ –≤ Telegram.\n 2. –ö–∞–∫–∏–µ –¥–∞–Ω–Ω—ã–µ –º—ã —Å–æ–±–∏—Ä–∞–µ–º: –ü—Ä–∏ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–∏ "–ù–æ–º–µ—Ä–æ–∞–≤—Ç–æ" –º—ã –º–æ–∂–µ–º —Å–æ–±–∏—Ä–∞—Ç—å —Å–ª–µ–¥—É—é—â–∏–µ —Ç–∏–ø—ã –¥–∞–Ω–Ω—ã—Ö:\n-–§–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏ –∞–≤—Ç–æ–º–æ–±–∏–ª–µ–π: –í—ã –º–æ–∂–µ—Ç–µ –∑–∞–≥—Ä—É–∂–∞—Ç—å —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏ –∞–≤—Ç–æ–º–æ–±–∏–ª–µ–π, –∫–æ—Ç–æ—Ä—ã–µ –º—ã –∏—Å–ø–æ–ª—å–∑—É–µ–º –¥–ª—è –ø—Ä–µ–¥–æ—Å—Ç–∞–≤–ª–µ–Ω–∏—è –Ω–∞—à–µ–π —É—Å–ª—É–≥–∏.\n-–ú–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ: –ú—ã —Ç–∞–∫–∂–µ –º–æ–∂–µ–º —Å–æ–±–∏—Ä–∞—Ç—å –º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ, –≤–∫–ª—é—á–∞—è –¥–∞—Ç—É, –≤—Ä–µ–º—è –∏ –º–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏–µ, –∫–æ–≥–¥–∞ –±—ã–ª–∞ —Å–¥–µ–ª–∞–Ω–∞ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏ (–µ—Å–ª–∏ –¥–æ—Å—Ç—É–ø–Ω–æ).\n-–ù–∏–∫–Ω–µ–π–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è: –í–∞—à –Ω–∏–∫–Ω–µ–π–º –≤ Telegram –±—É–¥–µ—Ç –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å—Å—è –¥–ª—è –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–∏—è —Å –±–æ—Ç–æ–º.\n 3. –ö–∞–∫ –º—ã –∏—Å–ø–æ–ª—å–∑—É–µ–º –≤–∞—à–∏ –¥–∞–Ω–Ω—ã–µ: –ú—ã –∏—Å–ø–æ–ª—å–∑—É–µ–º —Å–æ–±—Ä–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –¥–ª—è —Å–ª–µ–¥—É—é—â–∏—Ö —Ü–µ–ª–µ–π: –ü—Ä–µ–¥–æ—Å—Ç–∞–≤–ª–µ–Ω–∏–µ —É—Å–ª—É–≥–∏: –ß—Ç–æ–±—ã –ø–æ–∫–∞–∑–∞—Ç—å –≤–∞–º –º–∞—Ä–∫—É –∏ –º–æ–¥–µ–ª—å –∞–≤—Ç–æ–º–æ–±–∏–ª—è –ø–æ –∑–∞–≥—Ä—É–∂–µ–Ω–Ω—ã–º —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏—è–º.\n-–£–ª—É—á—à–µ–Ω–∏–µ —Å–µ—Ä–≤–∏—Å–∞: –î–ª—è –∞–Ω–∞–ª–∏–∑–∞ –∏ —É–ª—É—á—à–µ–Ω–∏—è —Ä–∞–±–æ—Ç—ã –Ω–∞—à–µ–≥–æ –±–æ—Ç–∞.\n-–ü–æ–¥–¥–µ—Ä–∂–∫–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π: –î–ª—è –æ–∫–∞–∑–∞–Ω–∏—è –ø–æ–¥–¥–µ—Ä–∂–∫–∏ –∏ –æ—Ç–≤–µ—Ç–æ–≤ –Ω–∞ –≤–∞—à–∏ –≤–æ–ø—Ä–æ—Å—ã.\n 4. –ë–µ–∑–æ–ø–∞—Å–Ω–æ—Å—Ç—å –¥–∞–Ω–Ω—ã—Ö: –ú—ã –ø—Ä–∏–Ω–∏–º–∞–µ–º —Ä–∞–∑—É–º–Ω—ã–µ –º–µ—Ä—ã –¥–ª—è –∑–∞—â–∏—Ç—ã –≤–∞—à–∏—Ö –¥–∞–Ω–Ω—ã—Ö –æ—Ç –Ω–µ—Å–∞–Ω–∫—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ –¥–æ—Å—Ç—É–ø–∞, –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –∏–ª–∏ —Ä–∞—Å–∫—Ä—ã—Ç–∏—è. –û–¥–Ω–∞–∫–æ, –ø–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–º–Ω–∏—Ç–µ, —á—Ç–æ –Ω–∏–∫–∞–∫–æ–π —Å–ø–æ—Å–æ–± –ø–µ—Ä–µ–¥–∞—á–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç—É –∏–ª–∏ —Å–ø–æ—Å–æ–± —Ö—Ä–∞–Ω–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –Ω–µ —è–≤–ª—è–µ—Ç—Å—è 100% –±–µ–∑–æ–ø–∞—Å–Ω—ã–º.\n 5. –•—Ä–∞–Ω–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö: –ú—ã —Ö—Ä–∞–Ω–∏–º –≤–∞—à–∏ –¥–∞–Ω–Ω—ã–µ —Ç–æ–ª—å–∫–æ —Å—Ç–æ–ª—å–∫–æ –≤—Ä–µ–º–µ–Ω–∏, —Å–∫–æ–ª—å–∫–æ —ç—Ç–æ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –¥–ª—è –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è —Ü–µ–ª–µ–π, —É–∫–∞–∑–∞–Ω–Ω—ã—Ö –≤ —ç—Ç–æ–π –ü–æ–ª–∏—Ç–∏–∫–µ –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏. –ü–æ—Å–ª–µ —ç—Ç–æ–≥–æ –≤–∞—à–∏ –ª–∏—á–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –±—É–¥—É—Ç —É–¥–∞–ª–µ–Ω—ã –∏–ª–∏ –∞–Ω–æ–Ω–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω—ã.\n 6. –ü–µ—Ä–µ–¥–∞—á–∞ –¥–∞–Ω–Ω—ã—Ö —Ç—Ä–µ—Ç—å–∏–º –ª–∏—Ü–∞–º: –ú—ã –Ω–µ –ø–µ—Ä–µ–¥–∞–µ–º –≤–∞—à–∏ –¥–∞–Ω–Ω—ã–µ —Ç—Ä–µ—Ç—å–∏–º –ª–∏—Ü–∞–º –±–µ–∑ –≤–∞—à–µ–≥–æ —Å–æ–≥–ª–∞—Å–∏—è, –∑–∞ –∏—Å–∫–ª—é—á–µ–Ω–∏–µ–º —Å–ª—É—á–∞–µ–≤, –∫–æ–≥–¥–∞ —ç—Ç–æ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –¥–ª—è –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è –Ω–∞—à–∏—Ö –æ–±—è–∑–∞—Ç–µ–ª—å—Å—Ç–≤ –ø–µ—Ä–µ–¥ –≤–∞–º–∏, –∏–ª–∏ –µ—Å–ª–∏ —ç—Ç–æ —Ç—Ä–µ–±—É–µ—Ç—Å—è –ø–æ –∑–∞–∫–æ–Ω—É. –ú—ã –Ω–µ –Ω–µ—Å—ë–º –æ—Ç–≤–µ—Ç—Å–≤–µ–Ω–Ω–æ—Å—Ç–∏ –∑–∞ –ø–æ–ª–Ω—É—é —Å–æ—Ö—Ä–∞–Ω–Ω–æ—Å—Ç—å –≤–∞—à–∏—Ö –ª–∏—á–Ω—ã—Ö –¥–∞–Ω–Ω—ã–π, –∫–æ—Ç–æ—Ä—ã–µ –≤–∏–¥–Ω—ã –≤ –∞–∫–∫–∞—É–Ω—Ç–µ!\n 7. –ò–∑–º–µ–Ω–µ–Ω–∏—è –≤ –ø–æ–ª–∏—Ç–∏–∫–µ –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏: –ú—ã –º–æ–∂–µ–º –ø–µ—Ä–∏–æ–¥–∏—á–µ—Å–∫–∏ –æ–±–Ω–æ–≤–ª—è—Ç—å —ç—Ç—É –ü–æ–ª–∏—Ç–∏–∫—É –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏. –ú—ã –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ —É–≤–µ–¥–æ–º–∏–º –≤–∞—Å –æ –ª—é–±—ã—Ö –∏–∑–º–µ–Ω–µ–Ω–∏—è—Ö, —Ä–∞–∑–º–µ—â–∞—è –æ–±–Ω–æ–≤–ª–µ–Ω–Ω—É—é –≤–µ—Ä—Å–∏—é –∑–¥–µ—Å—å. –†–µ–∫–æ–º–µ–Ω–¥—É–µ–º –ø–µ—Ä–∏–æ–¥–∏—á–µ—Å–∫–∏ –ø—Ä–æ–≤–µ—Ä—è—Ç—å —ç—Ç—É —Å—Ç—Ä–∞–Ω–∏—Ü—É –Ω–∞ –ø—Ä–µ–¥–º–µ—Ç –ª—é–±—ã—Ö –æ–±–Ω–æ–≤–ª–µ–Ω–∏–π.\n 8. –ö–æ–Ω—Ç–∞–∫—Ç–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ: –ï—Å–ª–∏ —É –≤–∞—Å –µ—Å—Ç—å –≤–æ–ø—Ä–æ—Å—ã –∏–ª–∏ –ø–æ–∂–µ–ª–∞–Ω–∏—è –ø–æ –ø–æ–≤–æ–¥—É –Ω–∞—à–µ–π –ü–æ–ª–∏—Ç–∏–∫–∏ –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏, –ø–æ–∂–∞–ª—É–π—Å—Ç–∞, —Å–≤—è–∂–∏—Ç–µ—Å—å —Å –Ω–∞–º–∏ —á–µ—Ä–µ–∑ Telegram-–±–æ—Ç–∞ –∏–ª–∏ –ø–æ —Å–ª–µ–¥—É—é—â–µ–º—É –∞–¥—Ä–µ—Å—É —ç–ª–µ–∫—Ç—Ä–æ–Ω–Ω–æ–π –ø–æ—á—Ç—ã: celp064@gmai.com\n 9. –°–æ–≥–ª–∞—Å–∏–µ –Ω–∞ –æ–±—Ä–∞–±–æ—Ç–∫—É –¥–∞–Ω–Ω—ã—Ö –ò—Å–ø–æ–ª—å–∑—É—è "–ù–æ–º–µ—Ä–æ–∞–≤—Ç–æ –†–ë", –≤—ã –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–∞–µ—Ç–µ, —á—Ç–æ –æ–∑–Ω–∞–∫–æ–º–ª–µ–Ω—ã —Å –¥–∞–Ω–Ω–æ–π –ü–æ–ª–∏—Ç–∏–∫–æ–π –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏ –∏ —Å–æ–≥–ª–∞—à–∞–µ—Ç–µ—Å—å —Å —É—Å–ª–æ–≤–∏—è–º–∏ –æ–±—Ä–∞–±–æ—Ç–∫–∏ –≤–∞—à–∏—Ö –¥–∞–Ω–Ω—ã—Ö.\n \n <b>–ù–∞–ø–∏—à–∏—Ç–µ</b> <u>–ü–†–ò–ù–Ø–¢–¨</u>, –µ—Å–ª–∏ –≤—ã —Å–æ–≥–ª–∞—Å–Ω—ã —Å —É—Å–ª–æ–≤–∏—è–º–∏ –Ω–∞ –æ–±—Ä–∞–±–æ—Ç–∫—É –¥–∞–Ω–Ω—ã—Ö –∏–ª–∏ <u>–û–¢–ö–õ–û–ù–ò–¢–¨</u>, –µ—Å–ª–∏ –Ω–µ —Å–æ–≥–ª–∞—Å–Ω—ã.', parse_mode='html')

#–ò–Ω—Ñ–∞ –¥–ª—è —Ä–∞–∑—Ä–∞–±–∞
@bot.message_handler(commands=['kirilldevinfo'])
def main(message):
    bot.send_message(message.chat.id, message)
    bot.reply_to(message, '–≠–¢–û–¢ –ë–û–¢ –°–û–ó–î–ê–ù –ö–ò–†–ò–õ–õ–û–ú –ö–û–í–ê–õ–¨–ß–£–ö–û–ú! –ê –í–ê–ù–Ø ! \n')


@bot.message_handler()
def info(message):
    if message.text.lower() == '–æ—Ç–∫–ª–æ–Ω–∏—Ç—å':
        bot.send_message(message.chat.id, '‚ùå–í—ã –Ω–µ –ø—Ä–∏–Ω—è–ª–∏ –Ω–∞—à –∑–∞–ø—Ä–æ—Å –Ω–∞ –æ–±—Ä–∞–±–æ—Ç–∫—É –¥–∞–Ω–Ω—ã—Ö!‚ùå\n \n–í—ã –¥–æ–ª–∂–Ω—ã –¥–∞—Ç—å —Å–æ–≥–ª–∞—Å–∏–µ –Ω–∞ –æ–±—Ä–∞–±–æ—Ç–∫—É –¥–∞–Ω–Ω—ã—Ö. –ò–Ω–∞—á–µ –í—ã –Ω–µ —Å–º–æ–∂–µ—Ç–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç—å—Å—è –±–æ—Ç–æ–º!\n \n<u><b>–ù–∞–ø–∏—à–∏—Ç–µ</b> "–ü–†–ò–ù–Ø–¢–¨", –µ—Å–ª–∏ –≤—ã —Å–æ–≥–ª–∞—Å–Ω—ã —Å —É—Å–ª–æ–≤–∏—è–º–∏ –Ω–∞ –æ–±—Ä–∞–±–æ—Ç–∫—É –¥–∞–Ω–Ω—ã—Ö –∏–ª–∏ –º—ã –Ω–µ —Å–º–æ–∂–µ–º —Å –≤–∞–º–∏ —Å–æ—Ç—Ä—É–¥–Ω–∏—á–∞—Ç—å!</u>', parse_mode='html')
    elif message.text.lower() == '–ø—Ä–∏–Ω—è—Ç—å':
        markup = types.ReplyKeyboardMarkup()
        markup.add(types.KeyboardButton('–û—Ç–∫—Ä—ã—Ç—å Web-–ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ', web_app=WebAppInfo(url='https://kirill1029.github.io/')))
        bot.send_message(message.chat.id, '‚úÖ–í—ã –ø—Ä–∏–Ω—è–ª–∏ –Ω–∞—à –∑–∞–ø—Ä–æ—Å –Ω–∞ –æ–±—Ä–∞–±–æ—Ç–∫—É –¥–∞–Ω–Ω—ã—Ö!‚úÖ\n \n‚¨á –¢–µ–ø–µ—Ä—å –≤—ã –º–æ–∂–µ—Ç–µ –æ—Ç–∫—Ä—ã—Ç—å –Ω–∞—à–µ Web-–ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ ‚¨á\n ', parse_mode='html', reply_markup=markup)


bot.polling(none_stop=True)





markup = types.InlineKeyboardMarkup()
button_accept = markup.add(types.InlineKeyboardButton('‚úÖ–Ø –ø—Ä–∏–Ω–∏–º–∞—é –≤—Å–µ —É—Å–ª–æ–≤–∏—è‚úÖ', callback_data='readed'))
button_decline = markup.add(types.InlineKeyboardButton('‚ùå–Ø –Ω–µ –ø—Ä–∏–Ω–∏–º–∞—é —ç—Ç–∏ —É—Å–ª–æ–≤–∏—è‚ùå', callback_data='not-readed'))
markup.add(button_accept, button_decline)